import React from 'react';
import './style.css';

const Hero = () => {
    return (
        <div className="hero">
            <img src="caminho_para_foto_de_perfil" alt="Foto de Perfil" />
            <h1>Karla C. Ferreira</h1>
            <h2>Desenvolvedora Full-Stack</h2>
            <p>Estudante de Ciência da Computação e Engenharia de Software apaixonada
                por tecnologia e programação, especialmente pela área de automação e desenvolvimento
                de sistemas. Atualmente, estou focada em desenvolver minhas habilidades e conhecimentos
                para contribuir efetivamente para o setor de tecnologia da informação. Acredito que a
                tecnologia tem o poder de transformar o mundo e estou ansiosa para desempenhar um papel
                ativo nessa transformação.</p>
            <div className="buttons">
                <a href="link_para_github" className="btn">GitHub</a>
                <a href="link_para_linkedin" className="btn">LinkedIn</a>
                <a href="link_para_linkedin" className="btn">Instagram</a>
                <a href="link_para_download_pdf" className="btn">Download Currículo (PDF)</a>
            </div>
        </div>
    );
}

export default Hero;
