import React from 'react';
import './style.css';

const translations = {
  'pt-BR': {
    title: 'Interesses e Hobbies',
    categories: {
      "Desenvolvimento": "Desenvolvimento",
      "Infraestrutura": "Infraestrutura",
      "Segurança": "Segurança",
      "Análise de Dados": "Análise de Dados",
      "Gestão de Projetos": "Gestão de Projetos"
    },
    interests: {
      "Desenvolvimento de APIs": "Desenvolver e criar interfaces de programação de aplicativos eficientes e escaláveis.",
      "Otimização de Consultas SQL": "Aperfeiçoar consultas SQL para melhorar o desempenho e a eficiência das operações de banco de dados.",
      "Desenvolvimento Ágil e Scrum": "Aplicar metodologias ágeis como Scrum para gerenciar projetos de desenvolvimento de software de forma eficiente.",
      "DevOps e Automação de Infraestrutura": "Implementar práticas de DevOps para automatizar a infraestrutura de TI e acelerar o ciclo de desenvolvimento.",
      "Cloud Computing": "Utilizar serviços de computação em nuvem para escalabilidade, flexibilidade e disponibilidade de sistemas.",
      "Segurança de Dados": "Garantir a proteção e integridade dos dados por meio de práticas de segurança robustas e atualizadas.",
      "Segurança Cibernética": "Aprofundar os conhecimentos em segurança cibernética para proteger sistemas e dados contra ameaças digitais.",
      "Machine Learning e Análise de Dados": "Explorar técnicas de machine learning para análise preditiva e descoberta de insights em conjuntos de dados.",
      "Análise de Desempenho e Otimização de Aplicações": "Identificar gargalos de desempenho em aplicações e otimizá-las para melhor eficiência.",
      "Mineração de Dados e Big Data": "Explorar técnicas de mineração de dados e lidar com grandes volumes de dados para identificar padrões e tendências significativas.",
      "Gestão de Projetos de TI": "Adquirir habilidades em gestão de projetos para liderar equipes e entregar projetos dentro do prazo e orçamento estabelecidos."
    }
  },
  'en': {
    title: 'Interests and Hobbies',
    categories: {
      "Desenvolvimento": "Development",
      "Infraestrutura": "Infrastructure",
      "Segurança": "Security",
      "Análise de Dados": "Data Analysis",
      "Gestão de Projetos": "Project Management"
    },
    interests: {
      "Desenvolvimento de APIs": "Explore and create efficient and scalable application programming interfaces.",
      "Otimização de Consultas SQL": "Enhance SQL queries to improve database operation performance and efficiency.",
      "Desenvolvimento Ágil e Scrum": "Apply agile methodologies like Scrum to efficiently manage software development projects.",
      "DevOps e Automação de Infraestrutura": "Implement DevOps practices to automate IT infrastructure and accelerate development cycles.",
      "Cloud Computing": "Utilize cloud computing services for system scalability, flexibility, and availability.",
      "Segurança de Dados": "Ensure data protection and integrity through robust and up-to-date security practices.",
      "Segurança Cibernética": "Deepen knowledge in cybersecurity to protect systems and data against digital threats.",
      "Machine Learning e Análise de Dados": "Explore machine learning techniques for predictive analysis and insights discovery in data sets.",
      "Análise de Desempenho e Otimização de Aplicações": "Identify performance bottlenecks in applications and optimize them for better efficiency.",
      "Mineração de Dados e Big Data": "Explore data mining techniques and handle large data volumes to identify significant patterns and trends.",
      "Gestão de Projetos de TI": "Acquire project management skills to lead teams and deliver projects within established deadlines and budgets."
    }
  }
};

const Hobbies = ({ language }) => {
  const translation = translations[language];

  return (
    <div className="backend-interests-hobbies">
      <h2>{translation.title}</h2>
      {Object.keys(translation.categories).map((category, index) => (
        <div key={index}>
          <h3>{translation.categories[category]}</h3>
          <ul>
            {translation.interestsByCategory[category].map((item, idx) => (
              <li key={idx}>
                <div className="interest-item">
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default Hobbies;
