import React, { useState } from 'react';
import './App.css';
import Footer from './components/footer';
import Header from './components/header';
import Project from './components/project';
import About from './components/About';
import Experience from './components/experiencia';
import Formation from './components/formation';
import Hobbies from './components/hobbies';
import Courses from './components/courses';
import Event from './components/eventos';
import Hero from './components/hero';
import Tecnologias from './components/tecnologias';
import Skills from './components/skills';
import ContactComponent from './components/contact';
import Functions from './components/functions';

const App = () => {
  const [language, setLanguage] = useState('pt-BR');
  const [theme, setTheme] = useState('dark');

  const handleLanguageChange = () => {
    setLanguage(language === 'pt-BR' ? 'en' : 'pt-BR');
  };

  const handleThemeChange = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  return (
    <div className={`app ${theme === 'dark' ? 'dark-theme' : 'light-theme'}`}>
      <Header language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
      <main>
        <Functions language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Hero language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <About language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Experience language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Formation language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Tecnologias language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Skills language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Hobbies language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Project language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Courses language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <Event language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
        <ContactComponent language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
      </main>
      <Footer language={language} setLanguage={setLanguage} theme={theme} setTheme={setTheme} />
    </div>
  );
}

export default App;
