import React from 'react';
import './App.css';
import Footer from './components/footer';
import Formation from './components/formation';

const Navigation = () => {
  return (
    <nav>
      <h1>Karla Ferreira</h1>
      <ul>
        <li><a href="#">Home</a></li>
        <li><a href="#">Sobre</a></li>
        <li><a href="#">Formação</a></li>
        <li><a href="#">Projetos</a></li>
        <li><a href="#o">Contato</a></li>
      </ul>
    </nav>
  );
}

const Section = () => {
  return (
    <section>
      <h2>Olá! 👋 Sou a Karla Ferreira 👩🏻‍💻🤠</h2>
      <p>Estudante de Ciência da Computação e Engenharia de Software apaixonada 
        por tecnologia e programação, especialmente pela área de automação e desenvolvimento 
        de sistemas. Atualmente, estou focada em desenvolver minhas habilidades e conhecimentos 
        para contribuir efetivamente para o setor de tecnologia da informação. Acredito que a 
        tecnologia tem o poder de transformar o mundo e estou ansiosa para desempenhar um papel 
        ativo nessa transformação.</p>
      <a href="https://github.com/kfrural">Visite meu perfil no GitHub</a>
    </section>
  );
}

const ProjectSection = () => {
  return (
    <section>
      <h2>Meus Projetos</h2>
      <ul>
        <li>
          <h3>Projeto 1</h3>
          <p>Descrição do Projeto 1</p>
          <a href="https://github.com/kfrural/projeto1">Ver Projeto 1</a>
        </li>
        <li>
          <h3>Projeto 2</h3>
          <p>Descrição do Projeto 2</p>
          <a href="https://github.com/kfrural/projeto2">Ver Projeto 2</a>
        </li>
        {/* Adicione mais projetos aqui */}
      </ul>
    </section>
  );
}


const App = () => {
  return (
    <div>
      <header>
        <Navigation />
      </header>

      <main>
        <Section />
        <Formation />
        <ProjectSection />
        {/* Outras seções */}
      </main>

      <Footer />
    </div>
  );
}

export default App;
