import React from 'react';
import './style.css';
import profileImage from '../../assets/img.png';

const Hero = () => {
    return (
        <div className="hero">
            <div className="profile-image">
                <img src={profileImage} alt="Foto de Perfil" />
            </div>
            <h1>Karla C. Ferreira</h1>
            <h2>Desenvolvedora Full-Stack</h2>
            <p>
                Estudante de <strong>Ciência da Computação</strong> e <strong>Engenharia
                de Software</strong>, apaixonada por tecnologia e programação, especialmente
                pela área de automação e desenvolvimento de sistemas. Atualmente, estou focada em desenvolver
                minhas habilidades e conhecimentos para contribuir efetivamente para
                o setor de tecnologia da informação. Acredito que a tecnologia tem o
                poder de transformar o mundo e estou ansiosa para desempenhar um
                papel ativo nessa transformação. <strong>Vamos juntos nessa jornada!</strong>
            </p>

            <div className="buttons">
                <a href="https://github.com/kfrural/" className="btn">GitHub</a>
                <a href="https://www.linkedin.com/in/karla-ferreira-rural/" className="btn">LinkedIn</a>
                <a href="https://www.instagram.com/kf.rural/" className="btn">Instagram</a>
                <a href="link_para_download_pdf" className="btn">Download Currículo (PDF)</a>
            </div>
        </div>
    );
}

export default Hero;
