import React, { useState } from 'react';
import './style.css';

const ContactComponent = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [contact, setContact] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Nome:', name);
    console.log('E-mail:', email);
    console.log('Mensagem:', message);
    console.log('Contato:', contact);
    setName('');
    setEmail('');
    setMessage('');
    setContact('');
  };

  return (
    <section className="contact" id='contato'>
      <div className="contact-form">
        <h2>Formul√°rio de Contato</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              placeholder="Nome"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              placeholder="E-mail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <textarea
              placeholder="Mensagem"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            ></textarea>
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="Meio de contato preferido"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="submit-btn">Enviar</button>
        </form>
      </div>
      <div className="contact-info">
        <h2>Informa√ß√µes de Contato</h2>
        <div className="info-item">
          <i className="fas fa-envelope"></i>
          <p>E-mail: <a href="mailto:kferreira_@outlook.com?subject=Curr√≠culo Web&body=">kferreira_@outlook.com</a></p>
        </div>
        <div className="info-item">
          <i className="fab fa-linkedin"></i>
          <p>LinkedIn: <a href="https://www.linkedin.com/in/karla-ferreira-rural/">karla-ferreira-rural</a></p>
        </div>
        <div className="info-item">
          <i className="fab fa-github"></i>
          <p>GitHub: <a href="https://github.com/kfrural">kfrural</a></p>
        </div>
      </div>
    </section>
  );
}

export default ContactComponent;
